int escape0 (int i)
{
    if (ansi) {
	strcpy (bufesc + i, "\033[0m");
	i += 4;
    }
    return i;
}

int escape1 (int i)
{
    if (ansi) {
	strcpy (bufesc + i, "\033[31;1m");
	i += 7;
    } else {
	bufesc [i++] = ' ';
	bufesc [i++] = ' ';
    }
    return i;
}

char *escape ()
{
    int
	i,
	j,
	n;

    if (errorbase == 0) {
	j = 0;
	for (i = bufscan1; buffer [i]; i++) {
	    if (i == bufscan2)
		j = escape1 (j);
	    bufesc [j++] = buffer [i];
	}
	if (i == bufscan2)
	    j = escape1 (j);
	j = escape0 (j);
	bufesc [j] = '\0';
	return bufesc;
    }

    j = 0;
    for (i = bufscan1; buffer [i]; i++) {

	if (i == bufscan2)
	    j = escape1 (j);
	
	n = (unsigned char) buffer [i];

	if (n > 32 && n < 127 && buffer [i] != '\\') {
	    bufesc [j++] = buffer [i];
	    continue;
	}

	bufesc [j++] = '\\';
	switch (errorbase) {
	    case 8:
		sprintf (bufesc + j, "%03o", n);
		j += 3;
		break;
	    case 10:
		bufesc [j++] = 'd';
		sprintf (bufesc + j, "%03d", n);
		j += 3;
		break;
	    case 16:
		bufesc [j++] = 'x';
		sprintf (bufesc + j, "%02X", n);
		j += 2;
		break;
	}

    }
    if (i == bufscan2)
	j = escape1 (j);
    j = escape0 (j);
    bufesc [j] = '\0';
    return bufesc;

}

char *escape2 (char *s)
{
    int
	i,
	j,
	n;

    if (errorbase == 0)
	return s;

    j = 0;
    for (i = 0; s [i]; i++) {

	n = (unsigned char) s [i];

	if (n > 32 && n < 127 && s [i] != '\\') {
	    bufesc [j++] = s [i];
	    continue;
	}

	bufesc [j++] = '\\';
	switch (errorbase) {
	    case 8:
		sprintf (bufesc + j, "%03o", n);
		j += 3;
		break;
	    case 10:
		bufesc [j++] = 'd';
		sprintf (bufesc + j, "%03d", n);
		j += 3;
		break;
	    case 16:
		bufesc [j++] = 'x';
		sprintf (bufesc + j, "%02X", n);
		j += 2;
		break;
	}

    }
    bufesc [j] = '\0';
    return bufesc;

}

char *unescape (char *s)
{
    char
	tmp [4];
    unsigned char
	uc;
    int
	i,
	j,
	d,
	si;
    unsigned int
	u;
    BOOL_
	err;

    if (! tokenesc)
	return s;

    tmp [3] = '\0';

    for (i = j = 0; s [i]; i++) {

	if (s [i] != '\\') {
	    bufesc [j++] = s [i];
	    continue;
	}

	i++;

	if (s [i] == '\\') {
	    bufesc [j++] = '\\';
	    continue;
	}

	err = FALSE;
	si = u = d = 0;
	if (s [i] == 'd' || s [i] == 'D') {
	    if (isdigit ((unsigned char) s [i + 1]) &&
		isdigit ((unsigned char) s [i + 2]) &&
		isdigit ((unsigned char) s [i + 3])
	    ) {
		memcpy (tmp, s + i + 1, 3);
		sscanf (tmp, "%d", &si);
		u = si;
		d = 3;
	    } else
		err = TRUE;
	} else if (s [i] == 'x' || s [i] == 'X') {
	    if (isxdigit ((unsigned char) s [i + 1]) &&
		isxdigit ((unsigned char) s [i + 2])
	    ) {
		memcpy (tmp, s + i + 1, 2);
		tmp [2] = '\0';
		sscanf (tmp, "%x", &u);
		d = 2;
	    } else
		err = TRUE;
	} else if ((s [i    ] >= '0' && s [i    ] <= '7') &&
		   (s [i + 1] >= '0' && s [i + 1] <= '7') &&
		   (s [i + 2] >= '0' && s [i + 2] <= '7')
	) {
	    memcpy (tmp, s + i, 3);
	    sscanf (tmp, "%o", &u);
	    d = 2;
	} else
	    err = TRUE;

	if (err)
	    errit (e_STOP, "Illegal escape sequence: %s", s + i - 1);
	if (u < 1 || u > 255)
	    errit (e_STOP, "Escape sequence out of range: %s", s + i - 1);

	uc = (unsigned char) u;

	if (isspace (uc))
	    errit (e_STOP, "Illegal escape sequence (white space): %s", s + i - 1);

	bufesc [j++] = uc;
	i += d;
    }

    bufesc [j] = '\0';

    return bufesc;
}

void encodespace (int offset)
{
    int
	i,
	j;
    BOOL_
	found;

    found = FALSE;
    j = 0;
    for (i = offset; buffer [i]; i++) {
	if (buffer [i] == ' ') {
	    strcpy (buffr2 + j, "[" "[SP]" "]");  /* SPACE REQUIRED FOR FLEX/M4 */
	    j += 6;
	    found = TRUE;
	} else {
	    buffr2 [j++] = buffer [i];
	}
    }
    if (found) {
	buffr2 [j] = '\0';
	strcpy (buffer + offset, buffr2);
    }
}


void process_args ()
{
    while (arg_c > 1 && arg_v [1][0] == '-') {
        switch (arg_v [1][1]) {
	    case 'a':
	        ansi = TRUE;
		break;
            case 'c':
                errorstop = e_GO;
                break;
            case 'C':
                errorstop = e_COUNTDOWN;
		errorcount = atoi (get_arg ());
                break;
	    case 'd':
	        errorbase = 10;
		break;
	    case 'e':
		logerrors = get_arg ();
		break;
	    case 'f':
		logfeatures = get_arg ();
		break;
	    case 'g':
		toINT = FALSE;
		break;
	    case 'l':
	        showlabels = TRUE;
		break;
	    case 'o':
	        errorbase = 8;
		break;
	    case 't':
		logtokens = get_arg ();
		break;
	    case 'T':
		tokenized = TRUE;
		break;
	    case 'x':
	        errorbase = 16;
		break;
            default:
                errit (e_STOP, "Illegal option '%s'", arg_v [1]);
        }
	arg_c--;
	arg_v++;
    }
}

char *get_arg ()
{
    if (arg_v [1][2])
        return arg_v [1] + 2;

    if (arg_c == 2)
        errit (e_STOP, "Missing argument for '%s'", arg_v [1]);

    arg_v++;
    arg_c--;
    return arg_v [1];
}

void mexit (int retval)
{
    if (fperrors) {
	printf ("Errors written to file \"%s\"\n", logerrors);
	fclose (fperrors);
    }
    exit (retval);
}

void errit (es_TYPE stop, char const *format, ...)
{
    int
	i;
    va_list
	list;

    if (showlabels) {
	for (i = strlen (currentlabel); i > 0; i--)
	    if (isspace ((unsigned char) currentlabel [i - 1]))
		currentlabel [i - 1] = '\0';
	    else
		break;
    }

    if (lineno)
	emessage ("%s:%li:%s", infile, lineno, prefix);
    else
	emessage ("\nError %s: ", programname);

    va_start (list, format);
    vfprintf (stderr, format, list);
    if (showlabels)
	fprintf (stderr, "  %s", currentlabel);
    fprintf (stderr, lineno ? "\n" : "\n\n");

    if (fperrors) {
	va_start (list, format);
	vfprintf (fperrors, format, list);
	if (showlabels)
	    fprintf (fperrors, "  %s", currentlabel);
	fprintf (fperrors, lineno ? "\n" : "\n\n");
    }

    if (stop == e_STOP)
	mexit (1);

    erred = TRUE;
    errsum++;

    if (stop == e_GO)
	return;

    if (--errorcount <= 0) {
	emessage ("Too many errors, STOP\n\n");
	mexit (1);
    }
}

void emessage (char const *format, ...)
{
    va_list
	list;

    va_start (list, format);
    vfprintf (stderr, format, list);
    if (fperrors) {
	va_start (list, format);
	vfprintf (fperrors, format, list);
    }
}

void warning (char const *format, ...)
{
    va_list
	list;

    emessage ("%s:%li: warning: ", infile, lineno);

    va_start (list, format);
    vfprintf (stderr, format, list);
    if (fperrors) {
	va_start (list, format);
	vfprintf (fperrors, format, list);
    }

    emessage ("\n");
}

